mapper.select:
select

  distinct


from brms_rule_action




  order by ${example.orderByClause}


  limit #{limit}

mapper.insert:
insert into brms_rule_action (id, rule_key, attr_name, 
  action_value, action_value_type, value_calculate_formula, 
  calculate_scale, calculate_rounding_mode, 
  is_delete, create_time, update_time
  )
values (#{id,jdbcType=BIGINT}, #{ruleKey,jdbcType=VARCHAR}, #{attrName,jdbcType=VARCHAR}, 
  #{actionValue,jdbcType=VARCHAR}, #{actionValueType,jdbcType=VARCHAR}, #{valueCalculateFormula,jdbcType=VARCHAR}, 
  #{calculateScale,jdbcType=TINYINT}, #{calculateRoundingMode,jdbcType=VARCHAR}, 
  #{deleted,jdbcType=BIT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
